import React, { Component } from 'react';
import axios from "axios";
import Table from "react-bootstrap/Table";

class Home extends Component {
    state = {
        products: [],
    }

    componentDidMount() {

        // On recupere la liste entiere
        axios.get(`http://localhost:4000/products/1`)
            .then(res => {
                this.setState({ products: res.data });
                // insere les data dans products    

            })
    }

    render() {
        return (
            <div>
                <h1>Bienvenue sur le site Jolieplante</h1>
                {console.log(this.state.products)}
                {/*.map itere le tableau product */}
                { this.state.products.map(elem => {

                    // parcours le tableau product grace a .map et insere dans elem
                    return (
                        <Table striped bordered hover>
                            <div>
                                <thead>
                                    <tr>
                                        <th>Name</th>
                                        <th>Description</th>
                                        <th>Category</th>
                                        <th>Picture</th>
                                        <th>Price</th>
                                        <th>Action</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    <tr>
                                        <td>{elem.productName}</td>
                                        <td>{elem.description}</td>
                                        <td>{elem.categorie}</td>
                                        <td><img src={elem.photo} alt="" sizes="5em" srcset=""/></td>
                                    </tr>

                                </tbody></div>
                        </Table>
                    )



                })}
            </div>
        )
    }
}

export default Home;
