import React, { Component } from 'react';
import axios from "axios";
import Table from "react-bootstrap/Table";







class Home extends Component {
    state = {
        products: [],
    }

    componentDidMount() {

        // On recupere la liste entiere
        axios.get(`http://localhost:4000/allproducts/1`)
            .then(res => {
                this.setState({ products: res.data });
                // insere les data dans products    

            })
    }


    render() {
        return (
            <div>
                <h1>Bienvenue sur le site Jolieplante</h1>
                {console.log(this.state.products)}
                <h1> Hello </h1>
                {/*.map itere le tableau product */}
                { this.state.products.map(elem => {
                    // parcours le tableau product grace a .map et insere dans elem
                    return (
                <Table striped bordered hover>
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Description</th>
                            <th>Category</th>
                            <th>Picture</th>
                            <th>Price</th>
                            <th>Action</th>

                        </tr>
                    </thead>
                    <tbody>
                        <tr>
                            <td>1</td>
                            <td>Mark</td>
                            <td>Otto</td>
                            <td>@mdo</td>
                        </tr>
                        <tr>
                            <td>2</td>
                            <td>Jacob</td>
                            <td>Thornton</td>
                            <td>@fat</td>
                        </tr>
                        <tr>
                            <td>3</td>
                            <td colSpan="2">Larry the Bird</td>
                            <td>@twitter</td>
                        </tr>
                    </tbody>
                </Table>
                                    )
                                })}
            </div>
        )
    }
}

export default Home;
