import React, { Component } from 'react';
import { Route, Link } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import '../css/Header.css'
import SignUp from './SignUp'
import SignIn from './SignIn'
import Home from './Home'
import Dashboard from './Dashboard'
import ProductList from './ProductList'
import ProductPage from './ProductPage'
import CreateProduct from './CreateProducts'



class App extends Component {
  render() {
    return (
      <div>
        <ul>
          {/* Creation du local storage */}
          {!localStorage.getItem("token") ? (
            // si il n'y a pas de token loguer ca
            <span>
              <li><Link to="/Sign-Up">S'enregistrer</Link></li>
              <li><Link to="/Sign-In">Se connecter</Link></li>
            </span>
              // sinon loguer ca
          ) : (
              <span>
                <li><Link to="/Home">Page d'Accueil</Link></li>
                <li><Link to="/Dashboard">Dashboard</Link></li>
                <li><Link to="/ProductList">ProductList</Link></li>
                <li><Link to="/ProductPage">ProductPage</Link></li>
                <li><Link to="/CreateProduct">CreateProduct</Link></li>
                <li><Link to="/SignIn">Logout</Link></li>

              </span>
            )}

        </ul>

        <Route path="/Home" component={Home} />
        <Route path="/Sign-In" component={SignIn} />
        <Route path="/Sign-Up" component={SignUp} />
        <Route path="/Dashboard" component={Dashboard} />
        <Route path="/ProductList" component={ProductList} />
        <Route path="/ProductPage/:id" component={ProductPage} />
        <Route path="/CreateProduct" component={CreateProduct} />
        <Route path="/Logout"component={SignIn} />

        {/* :id nous permet de recuperer id de la bdd dans notre url ETAPE 1 */}



      </div>
    );
  }
}


export default App;
